import React, { useEffect } from 'react'
import styled from 'styled-components'
import { InnertLayout } from '../../styles/Layout';
import { useGlobalContext } from '../../context/globalContext';
import Form from '../Form/Form';
import ExpensesItem from '../ExpensesItem/ExpensesItem';

function Expenses() {
    const {addExpenses, incomes, getExpensess, deleteExpenses, totalExpenses} = useGlobalContext()

    useEffect(() => {
      getExpensess()
    }, [])

  return (
    <ExpensesStyled>
      <InnertLayout>
        <h1>Expensess</h1>
        <div className='total-income'>Total Expenses: <span>${totalExpenses()}</span></div>
        <div className='income-content'>
          <div className='form-container'>
            <Form />
          </div>

          <div className='incomes'>
            {incomes.map((income) => {
              const {_id, title, amount, date, category, description} = income;
              return <ExpensesItem 
                key={_id}
                id={_id}
                title={title}
                description={description}
                amount={amount}
                date={date}
                category={category}
                indicatorColor="var(--color-green)"
                deletItem={deleteExpenses}
              />
            })}
          </div>
        </div>
      </InnertLayout>
    </ExpensesStyled>
  )
}

const ExpensesStyled = styled.div`
  display: flex;
  overflow: auto;

  .total-income{
    display: flex;
    justify-content: center;
    align-items: center;
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    border-radius: 20px;
    padding: 1rem;
    margin: 1rem 0;
    font-size: 2rem;
    gap: .5rem;

    span{
      font-size: 2rem;
      font-weight: 800;
      color: var(--color-green);
    }
  }

  .income-content{
    display: flex;
    gap: 2rem;

    .incomes{
      flex: 1;
    }
  }
`;

export default Expenses
